function calcSwap() {
  let t = parseFloat(document.getElementById("ram-select").value);
  var e = document.getElementById("hibernationChecker").checked;
  t = Math.max(t, 0);
  var o = Math.round(Math.sqrt(t));
  e && (o += t), setLabel(o.toString());
}
function setLabel(t) {
  document.getElementById("min-swap").innerHTML = t.concat(" GB");
}
calcSwap();
var tooltipTriggerList = [].slice.call(
    document.querySelectorAll('[data-bs-toggle="tooltip"]')
  ),
  tooltipList = tooltipTriggerList.map(function (t) {
    return new bootstrap.Tooltip(t);
  });
